mapper.insert:
INSERT INTO ratings (
    script_id, user_id, content_score, market_score, commercial_score, 
    compliance_score, total_score, grade, comments
) VALUES (
    #{scriptId}, #{userId}, #{contentScore}, #{marketScore}, #{commercialScore},
    #{complianceScore}, #{totalScore}, #{grade}, #{comments}
)

mapper.select:
SELECT AVG(total_score) 
FROM ratings 
WHERE script_id = #{scriptId}

SELECT MAX(total_score) 
FROM ratings 
WHERE script_id = #{scriptId}

SELECT COUNT(*) 
FROM ratings 
WHERE script_id = #{scriptId}
